#
# Problem 8
#
# Find the greatest product of five consecutive digits in the 1000-digit number.

import unittest
from operator import mul

class Test_Problem_8(unittest.TestCase):
	
	def test_greatest_product_in_12345_is_120(self):
		self.assertEqual(120, greatest_product_of_five_consecutive_digits(12345))

	def test_greatest_product_in_25678943_is_15120(self):
		self.assertEqual(15120, greatest_product_of_five_consecutive_digits(25678943))

	def test_greatest_product_in_11111011_is_1(self):
		self.assertEqual(1, greatest_product_of_five_consecutive_digits(11111011))

def greatest_product_of_five_consecutive_digits(n):
	""" 
	Computes greates product of five consecutive digits in n by
	converting n to a string and iterating through it
	"""
	stringified = str(n)
	product = 1
	for dc in range(len(stringified)-4):
		consecutive  = map(int, stringified[dc:dc+5])
		product = max(product,reduce(lambda x, y: x*y, consecutive))
	return product

